import { configureStore, createSlice } from "@reduxjs/toolkit";
import { nanoid } from 'nanoid';

const initialStateContacts = [
    { id: 'id-1', name: 'Rosie Simpson', number: '459-12-56' },
    { id: 'id-2', name: 'Hermione Kline', number: '443-89-12' },
    { id: 'id-3', name: 'Eden Clements', number: '645-17-79' },
    { id: 'id-4', name: 'Annie Copeland', number: '227-91-26' },
]

const counterSlice = createSlice({
    name: 'contacts',
    initialState:initialStateContacts,
    reducers: {
        addContacts: {
            reducer(state, action) { state.push(action.payload) },
            prepare(name, number) {
                return {
                    payload: {
                        name: name,
                        number: number,
                        id: nanoid(),
                    },
                };
            },
        },
        deleteContacts(state, action) { return state.filter(contact => contact.id !== action.payload) }
    }
});
const filterSlice = createSlice({
    name: 'filter',
    initialState: initialStateContacts,
    reducers: {
        filterContacts(state, action) {
            console.log(action);
            console.log(state);
            let normalizeFilter = action.payload.toLowerCase();
            return state.filter(contact => contact.name.toLowerCase().includes(normalizeFilter))
        }
    }

})
export const store = configureStore({
    reducer: {
        contacts: counterSlice.reducer,
        filter:filterSlice.reducer,

    }

})
export const { addContacts, deleteContacts } = counterSlice.actions;
export const { filterContacts } = filterSlice.actions
