import { configureStore, createAction, createReducer} from "@reduxjs/toolkit";
import { nanoid } from 'nanoid';

const initialState =
{
        name: 'Alex',
        number:' 56-348748-38428',
        id: `id-` + nanoid(),   
}
const addContacts = createAction('addContacts');
const myReducer = createReducer(initialState, builder => {
    builder.addCase(addContacts, (state, action) => {return [...state,action.payload] })
});
export const store = configureStore({
    reducer: {
        contacts: myReducer,

    }
