import { createStore } from "@reduxjs/toolkit";
const reducer = (state, action) => {
    if (action.type === 'addContacts') {
        return {...state, contacts: [state.contacts, action.payload]
    }
    }
    return state
}
export const store = createStore(reducer, {
    contacts: [],
    filter: ""
})





